sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    // Update unassigned workers after refresh of spw's
    var assignedWorkersIds = api.data.look_up_shift_position_workers.results.filter(function(it) {
    return it.shift_worker._reference.shift._reference.order_in_the_day.value == api.state.selected_shift
        && it.shift_position.value != 'unassigned'; //in case looping over replica list
    }).map(function(it) {
        return it.shift_worker._reference.worker.value;
    });

    var unassignedShiftWorkers = api.data.look_up_shift_workers.results.filter(function(it) {
        return it.shift._reference.order_in_the_day.value == api.state.selected_shift
            && !assignedWorkersIds.includes(it.worker.value);
    });

    // Show unassigned workers (simulate data from a "shift position worker" record)
    var unassignedWorkers = [];
    unassignedShiftWorkers.forEach(function(sw) {
        unassignedWorkers.push({
            _row_data: {
                displayValue: 'Unassigned',
                uniqueValue: 'unassigned'
            },
            shift_worker: {
                value: sw._row_data.uniqueValue,
                _reference: sw
            }
        });
    });
    api.setState('unassigned_workers', unassignedWorkers);

    // Todo: Also update 'unassigned_workers_replica'? Maybe that should be updated when "auto-save" button disabled
}
