sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  // Hide light red bg if worker not in same area or 'work performed'
  const SAME_AREA = 'same-area';
  const SAME_WORK_PERFORMED = 'same-work-performed';
	var repetitionStatus = api.state.position_repetition_status[api.item.value.shift_worker.value];
  return (repetitionStatus != SAME_AREA) && (repetitionStatus != SAME_WORK_PERFORMED);
}
