sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const NO_REPEAT = 'no-repeat';
    const SAME_AREA = 'same-area';
    const SAME_WORK_PERFORMED = 'same-work-performed';
    const SAME_POSITION = 'same-position';

    var position_repetition_status = {};
    api.data.look_up_shift_position_workers.results.forEach(spw => {
        var repetitionStatus;
        var shiftNumber = spw.shift_worker._reference.shift._reference.order_in_the_day.value;
        if (shiftNumber > 1) {
            // Find previous position
            var prevShiftPositionWorker = api.data.look_up_shift_position_workers.results.find(function(prevSpw) {
                return (prevSpw.shift_worker._reference.worker.value == spw.shift_worker._reference.worker.value
                    && prevSpw.shift_worker._reference.shift.value == api.data.look_up_shifts_for_selected_day.results[shiftNumber - 2]._row_data.uniqueValue);
            });

            if (prevShiftPositionWorker) {
                var prevArea = prevShiftPositionWorker.shift_position._reference.position._reference.area.value;
                var currArea = spw.shift_position._reference.position._reference.area.value;
                var prevWorkPerformed = prevShiftPositionWorker.shift_position._reference.position._reference.work_performed.value;
                var currWorkPerformed = spw.shift_position._reference.position._reference.work_performed.value;
                var prevPosition = prevShiftPositionWorker.shift_position._reference.position.value;
                var currPosition = spw.shift_position._reference.position.value;

                // Position repeated
                if (prevPosition == currPosition) {
                    repetitionStatus = SAME_POSITION;
                }
                // Area repeated
                else if (prevArea == currArea && currArea) {
                    repetitionStatus = SAME_AREA;
                }
                // 'Work performed' repeated
                else if (prevWorkPerformed == currWorkPerformed && currWorkPerformed) {
                    repetitionStatus = SAME_WORK_PERFORMED;
                }
                // Nothing is repeated
                else {
                    repetitionStatus = NO_REPEAT;
                }
            } else {
                // Worker not in previous shift
                repetitionStatus = NO_REPEAT;
            }
        } else {
            // First shift
            repetitionStatus = NO_REPEAT;
        }
        position_repetition_status[spw.shift_worker.value] = repetitionStatus;
    });
    api.setState('position_repetition_status', position_repetition_status);
}
