sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
	// Show positions from api.item.value.min - max
  return api.data.look_up_shift_positions.results.filter(function(it) {
    return it.order.value >= api.item.value.min && it.order.value <= api.item.value.max;
  });

  /*return [{
    "position": api.item.value.min <= 100 ? "Red" : "Point Alpha"
  },{
    "position": api.item.value.min <= 100 ? "Orange" : "Point Bravo"
  }];*/
}
