sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  // Get active worker
	var activeWorker = api.data.look_up_shift_workers.results.find(function(it) {
    return api.state.active_worker && (it.worker.value == api.state.active_worker.id);
  });

  // Get active worker's position in previous shift
  var morningShiftId = 1;
  var prevShiftPositionWorker = api.data.look_up_shift_position_workers.results.find(function(it) {
    // Get record with same worker and with previous 'order in the day' (all retrieved shift position workers are for selected day)
    return (activeWorker && (it.shift_worker._reference.worker.value == api.state.active_worker.id))
      && it.shift_worker._reference.shift._reference.order_in_the_day.value == morningShiftId;
  });

  var activeWorkerStr = (activeWorker && activeWorker.worker.displayValue) || '[no active worker]';
  var prevPositionStr = (activeWorker && prevShiftPositionWorker && ` (from ${prevShiftPositionWorker.shift_position._reference.position.displayValue})`) || '';
	return `The afternoon shift. ${activeWorkerStr}${prevPositionStr}`;
}
