sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
	var selectedShift = api.data.look_up_shifts_for_selected_day.results[api.state.selected_shift - 1];
  var startTime = new Date(selectedShift.start_time.displayValue).toLocaleTimeString('en-US', {timeStyle:'short'});
  var endTime = new Date(selectedShift.end_time.displayValue).toLocaleTimeString('en-US', {timeStyle:'short'});
	return `${startTime} - ${endTime}`;
}
