sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    api.setState('shift_position_workers_replica', JSON.parse(JSON.stringify(api.data.look_up_shift_position_workers.results))); //hack for triggering visual update caused by changing a JSON-type client state param
}
