sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
	if (!api.state.active_worker) {
		return;
	}

	var activeShiftNumber = api.state.selected_shift;
	if (event.name == 'UXF_TAB_SET#TAB_SELECTED') {
		// If changed shift tabs, get active shift from event payload
		activeShiftNumber = event.payload.selectedTabIndex + 1;
	}

    // Update the shift worker, spw, and position associated with the active worker
	var shiftWorker = api.data.look_up_shift_workers.results.find(sw => {
		return sw.shift._reference.order_in_the_day.value == activeShiftNumber
			&& sw.worker.value == api.state.active_worker.id;
	});
    api.state.active_worker.shiftWorker = (shiftWorker && shiftWorker._row_data.uniqueValue) || null;

	var shiftPositionWorker = api.data.look_up_shift_position_workers.results.find(spw => {
		return spw.shift_worker._reference.shift._reference.order_in_the_day.value == activeShiftNumber
			&& spw.shift_worker._reference.worker.value == api.state.active_worker.id;
	});
    api.state.active_worker.shiftPositionWorker = (shiftPositionWorker && shiftPositionWorker._row_data.uniqueValue) || null;
    api.state.active_worker.position = (shiftPositionWorker && shiftPositionWorker.shift_position._reference.position.value) || 'unassigned';

	// Might not need below line
	api.setState('active_worker', JSON.parse(JSON.stringify(api.state.active_worker)));
}
