sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    var selectedDate;
    if (event.elementId == 'transform_event_dates_into_select_data_1' && event.name == 'DATA_FETCH_SUCCEEDED') {
        // Set selected date on page load
        selectedDate = api.data.transform_event_dates_into_select_data_1.output[0];
        if (selectedDate && selectedDate.id) {
            api.setState('selected_date', selectedDate.id.slice(0, 10) +"@javascript:gs.dateGenerate('"+selectedDate.id.slice(0, 10)+"','start')@javascript:gs.dateGenerate('"+selectedDate.id.slice(0, 10)+"','end')");
            api.setState('selected_date_dropdown_item', selectedDate);
        }
    } else {
        // Set selected date when input changes
        selectedDate = event.payload.item;
        api.setState('selected_date', selectedDate.id.slice(0, 10) +"@javascript:gs.dateGenerate('"+selectedDate.id.slice(0, 10)+"','start')@javascript:gs.dateGenerate('"+selectedDate.id.slice(0, 10)+"','end')");
        api.setState('selected_date_dropdown_item', selectedDate);
    }
    api.data.look_up_shifts_for_selected_day.refresh(); //needs to be called explicitly AFTER setting 'selected_date'; seems like event handlers run asynchronously and thus can't force to run after 'update client state' handler
}
