sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
	var shiftDate = new Date(api.item.value.shift_worker._reference.shift.displayValue);
  var prevShiftPositionWorker = api.data.look_up_shift_position_workers.results.find(function(it) {
    var itDate = new Date(it.shift_worker._reference.shift.displayValue);

    // Get record with same worker, on same day, with one less 'order in the day'
    return (it.shift_worker._reference.worker.value == api.item.value.shift_worker._reference.worker.value
      && itDate.getMonth() == shiftDate.getMonth()
      && itDate.getDate() == shiftDate.getDate()
      && it.shift_worker._reference.shift._reference.order_in_the_day.value == api.item.value.shift_worker._reference.shift._reference.order_in_the_day.value - 1);
  });
  var currShiftPositionWorker = api.item.value;

  // Hide normal bg if any of the below are true:
  //   (1) previous position is same
  //   (2) previous position's 'area' is same, and not both null
  //   (3) previous position's 'work performed' is same, and not both null
  var prevArea = prevShiftPositionWorker.shift_position._reference.position._reference.area.value;
  var currArea = currShiftPositionWorker.shift_position._reference.position._reference.area.value;
  var prevWorkPerformed = prevShiftPositionWorker.shift_position._reference.position._reference.work_performed.value;
  var currWorkPerformed = currShiftPositionWorker.shift_position._reference.position._reference.work_performed.value;
  if (prevShiftPositionWorker.shift_position._reference.position.value == currShiftPositionWorker.shift_position._reference.position.value
    || (prevArea == currArea && currArea != null)
    || (prevWorkPerformed == currWorkPerformed && currWorkPerformed != null))
  {
    return true;
  } else {
    return false;
  }
}
