sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
	if (!api.state.worker_search_input) {
		return;
	}
    var shiftWorkers = api.data.look_up_shift_workers.results;
	var workerFoundInCurrentShift = shiftWorkers.find(wkr => {
		return wkr.worker.displayValue.toLowerCase() == api.state.worker_search_input.toLowerCase()
            && wkr.shift._reference.order_in_the_day.value == api.state.selected_shift;
	});

	// If find worker among current shift workers, don't do anything
	if (workerFoundInCurrentShift) {	
		api.setState('awaiting_add_worker_to_shift', false);
		api.emit('NOW_UXF_PAGE#SET_LOADING_STATE', {
			loading: false,
			label: null,
			action: null,
			nonBlocking: null
		});
		return;
	}

	// Search for worker in complete list
	var workerFound = api.data.look_up_workers.results.find(wkr => {
		return wkr._row_data.displayValue.toLowerCase() == api.state.worker_search_input.toLowerCase(); //Todo: Use worker id
	});
	var wkr;
	if (workerFound) {
		// Transfer worker values from "JSON-formatted GlideRecord" to "dropdown-item" style
		wkr = {
			id: workerFound._row_data.uniqueValue,
			label: workerFound._row_data.displayValue,
			adult: workerFound.adult.value
		};
	} else {
		// If worker doesn't exist yet, create artificial dropdown item
		wkr = {
			id: 'new-worker',
			label: api.state.worker_search_input,
			adult: !api.state.kid_status_input
		};
	}
	console.log('wkr');
	console.log(wkr);
	// Transfer shift values from "JSON-formatted GlideRecord" to "dropdown-item" style
	var shiftFound = api.data.look_up_shifts_for_selected_day.results[api.state.selected_shift - 1];
	console.log('shiftFound');
	console.log(shiftFound);
	var shft = {
		id: shiftFound._row_data.uniqueValue,
		label: shiftFound._row_data.displayValue
	};

	// Add worker to current shift
	api.data.add_worker_to_shift.execute({
		worker: wkr,
		shift: shft
	});
}
