sys_ux_data_broker_transform.script:
function transform(input) {
	var workers = [];
	input.workers.forEach(function(wkr) {
		workers.push({
			id: wkr._row_data.uniqueValue,
			label: wkr._row_data.displayValue,
			adult: wkr.adult.value,
			icon: !wkr.adult.value ? 'flag-event-fill' : ''
		});
	});
	return workers;
}
